import React, { useState } from 'react';
import { Paper, Typography, Button } from '@material-ui/core';
import BookAppointment from './BookAppointment'; // Import BookAppointment component
import './DoctorList.css'; // Import your DoctorList CSS file

const DoctorList = ({ doctors }) => {
  const [selectedDoctor, setSelectedDoctor] = useState(null); // State to store the selected doctor
  const [isBookAppointmentOpen, setBookAppointmentOpen] = useState(false); // State to manage modal visibility

  // Function to handle opening the modal
  const handleOpenBookAppointment = (doctor) => {
    setSelectedDoctor(doctor);
    setBookAppointmentOpen(true);
  };

  // Function to handle closing the modal
  const handleCloseBookAppointment = () => {
    setBookAppointmentOpen(false);
  };

  return (
    <div className="DoctorsTab">
      {doctors.map((doctor, index) => (
        <Paper key={index} elevation={3} className="doctor-paper">
          <Typography variant="h6">Doctor Name: {doctor.name}</Typography><br/>
          <Typography variant="subtitle1">Speciality: {doctor.speciality}</Typography>
          <Typography variant="subtitle1">Average Rating: {doctor.rating}</Typography>
          <div className="button-container">
            <Button 
              variant="contained" 
              color="primary" 
              className="book-appointment-button" 
              onClick={() => handleOpenBookAppointment(doctor)} // Open modal on click
            >
              Book Appointment
            </Button>
            <Button 
              variant="contained" 
              style={{ backgroundColor: 'green', color: 'white' }} 
              className="view-details-button"
            >
              View Details
            </Button>
          </div>
        </Paper>
      ))}
      {/* Render BookAppointment modal */}
      <BookAppointment 
        open={isBookAppointmentOpen} 
        handleClose={handleCloseBookAppointment} 
        doctorName={selectedDoctor ? selectedDoctor.name : ''} // Pass selected doctor name
      />
    </div>
  );
};

export default DoctorList;
