// Home.js
import React from 'react';
import { Tabs, Tab } from '@material-ui/core';
import './Home.css';
import { Paper, Typography, Button } from '@material-ui/core';

const Home = () => {
    const [activeTab, setActiveTab] = React.useState(0);

    const handleTabChange = (event, newValue) => {
        setActiveTab(newValue);
    };

    return (
        <div className="home-container">
            <Tabs value={activeTab} onChange={handleTabChange}>
                <Tab label="DOCTORS" />
                <Tab label="APPOINTMENT" />
            </Tabs>
            <div className="tab-content">
                {activeTab === 0 && (
                    <div className="doctors-tab-content">
                        {/* Content for Doctors tab */}
                        <h2>DOCTORS</h2>
                        <div className="DoctorsTab">
                            {doctors.map((doctor, index) => (
                                <Paper key={index} elevation={3} style={{ padding: '20px', marginBottom: '20px' }}>
                                <Typography variant="h6">{doctor.name}</Typography>
                                <Typography variant="subtitle1">Speciality: {doctor.speciality}</Typography>
                                <Typography variant="subtitle1">Average Rating: {doctor.rating}</Typography>
                                <Button variant="contained" color="primary" className="BookAppointmentButton">
                                    Book Appointment
                                </Button>
                                <Button variant="contained" className="ViewDetailsButton">
                                    View Details
                                </Button>
                                </Paper>
                            ))}
                            </div>
                    </div>
                )}
                {activeTab === 1 && (
                    <div className="appointment-tab-content">
                        {/* Content for Appointment tab */}
                        <h2>APPOINTMENT</h2>
                        {/* Add your appointment list here */}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Home;
