import React, { useState } from 'react';
import { Modal, FormControl, InputLabel, Input, Button } from '@material-ui/core';
import './Login.css';

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');

    const handleLogin = async () => {
        // Reset errors
        setEmailError('');
        setPasswordError('');

        // Basic validation
        if (!email) {
            setEmailError('Please fill out this field');
            return;
        }
        if (!password) {
            setPasswordError('Please fill out this field');
            return;
        }

        try {
            // Make API call to login endpoint
            const response = await fetch('http://localhost:8080/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ email, password })
            });
            alert(email, )
            if (!response.ok) {
                setPasswordError('Login failed');
            }

            const data = await response.json();

            // Store accessToken in localStorage or sessionStorage
            localStorage.setItem('accessToken', data.accessToken);

            // Call the onLogin function with the accessToken
            onLogin(data.accessToken);
            alert(data.accessToken)
        } catch (error) {
            console.error('Login failed:', error);
            // Handle login failure, show error message, etc.
        }
    };

    return (
        <div className="login-modal">
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="email">Email *</InputLabel>
                <Input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    error={!!emailError}
                />
                <span className="error">{emailError}</span>
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="password">Password *</InputLabel>
                <Input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    error={!!passwordError}
                />
                <span className="error">{passwordError}</span>
            </FormControl>
            <Button variant="contained" color="primary" style={{ margin: 'auto', display: 'block' }} onClick={handleLogin}>
                LOGIN
            </Button>
        </div>
    );
}

export default Login;
