// BookAppointment.js

import React from 'react';
import { Modal, Card, TextField, FormControl, Select, MenuItem, Button } from '@material-ui/core';

const BookAppointment = ({ open, handleClose, doctorName }) => {
  return (
    <Modal open={open} onClose={handleClose}>
      <Card style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', padding: '20px' }}>
        <h2>Book an Appointment</h2>
        <TextField label="Doctor's Name" value={doctorName} disabled fullWidth margin="normal" />
        {/* Add Date Picker Component */}
        {/* Add Time Slot Selector Component */}
        <FormControl fullWidth margin="normal">
          <TextField label="Medical History" multiline rows={4} variant="outlined" />
        </FormControl>
        <FormControl fullWidth margin="normal">
          <TextField label="Symptoms" multiline rows={4} variant="outlined" />
        </FormControl>
        <Button variant="contained" color="primary" fullWidth onClick={handleClose}>
          Confirm Appointment
        </Button>
      </Card>
    </Modal>
  );
};

export default BookAppointment;
