import React, { useState } from 'react';
import { Modal, Tabs, Tab, Box } from '@material-ui/core';
import Login from '../../screens/login/Login';
import Register from '../../screens/register/Register';
import './Authentication.css';

const AuthenticationModal = ({ isOpen, handleClose }) => {
    const [activeTab, setActiveTab] = useState(0);

    const handleTabChange = (event, newValue) => {
        setActiveTab(newValue);
    };

    return (
        <Modal open={isOpen} onClose={handleClose}>
            <Box
                sx={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    bgcolor: 'background.paper',
                    boxShadow: 24,
                    p: 4,
                    width: 400,
                    maxWidth: '90%',
                    outline: 'none',
                }}
            >
                <div class="modal-title" style={{ backgroundColor: 'purple'}}>
                    <h4 style={{ color: 'white', margin: 0}}>Authentication</h4>
                </div>
                <Tabs value={activeTab} onChange={handleTabChange} variant="fullWidth" indicatorColor="primary">
                    <Tab label="Login" />
                    <Tab label="Register" />
                </Tabs>
                {activeTab === 0 && <Login/>} {/* Pass props to Login component */}
                {activeTab === 1 && <Register />}
            </Box>
        </Modal>
    );
}

export default AuthenticationModal;
