// Register.js
import React, { useState } from 'react';
import { FormControl, InputLabel, Input, Button } from '@material-ui/core';
import './Register.css'

const Register = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [contactNumber, setContactNumber] = useState('');
    const [firstNameError, setFirstNameError] = useState('');
    const [lastNameError, setLastNameError] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [contactNumberError, setContactNumberError] = useState('');
    const [registrationMessage, setRegistrationMessage] = useState('');




    const isValidContactNumber = (contactNumber) => {
        // Regular expression for basic mobile number validation
        // You can enhance it as per your requirements
        const contactNumberRegex = /^\d{10}$/;
        return contactNumberRegex.test(contactNumber);
    };

    return (
        <div className="register-form">
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="firstName">First Name *</InputLabel>
                <Input
                    id="firstName"
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    error={!!firstNameError}
                />
                <span className="error">{firstNameError}</span>
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="lastName">Last Name *</InputLabel>
                <Input
                    id="lastName"
                    type="text"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    error={!!lastNameError}
                />
                <span className="error">{lastNameError}</span>
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="email">Email *</InputLabel>
                <Input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    error={!!emailError}
                />
                <span className="error">{emailError}</span>
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="password">Password *</InputLabel>
                <Input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    error={!!passwordError}
                />
                <span className="error">{passwordError}</span>
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="contactNumber">Mobile No. *</InputLabel>
                <Input
                    id="contactNumber"
                    type="text"
                    value={contactNumber}
                    onChange={(e) => setContactNumber(e.target.value)}
                    error={!!contactNumberError}
                />
                <span className="error">{contactNumberError}</span>
            </FormControl>
            <Button variant="contained" className="registerButton" style={{ margin: 'auto', display: 'block' }} color="primary" onClick={handleRegister}>
                REGISTER
            </Button>
            {registrationMessage && <p>{registrationMessage}</p>}
        </div>
    );
};

export default Register;
