import React, { useState, useEffect } from 'react';
import { Modal, Card, TextField, FormControl, Select, MenuItem, Button } from '@material-ui/core';

const BookAppointment = ({ open, handleClose, doctor_name }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [doctorName, setDoctorName] = useState(null);

  useEffect(() => {
    // Set the doctorName state when the component mounts
    setDoctorName(doctor_name);
    // Log the chosen doctor name
    console.log("Chosen Doctor Name:", doctor_name);
  }, [doctor_name]); // Add doctor_name to the dependency array to watch for changes

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeSlotChange = (event) => {
    setSelectedTimeSlot(event.target.value);
  };

  const handleConfirmAppointment = () => {
    // Your logic to check if the time slot is already booked or not available
    if (selectedTimeSlot === 'booked') {
      setErrorMessage('Either the slot is already booked or not available');
    } else {
      // Your logic to confirm the appointment
      handleClose();
    }
  };

  return (
    <Modal open={open} onClose={handleClose}>
      <Card style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', padding: '20px' }}>
        <h2>Book an Appointment</h2>
        {/* Display the doctor's name from the state */}
        <TextField label="Doctor's Name" value={doctorName} disabled fullWidth margin="normal" />
        {/* Rest of the JSX code... */}
      </Card>
    </Modal>
  );
};

export default BookAppointment;
