// Header.js
import React, { useState, useEffect } from 'react';
import './Header.css';
import logo from '../../assets/logo.jpeg';
import Button from '@material-ui/core/Button';
import AuthenticationModal from '../../screens/authentication/AuthenticationModal';
import Home from '../../screens/home/Home';


const Header = () => {
    const [isLoginModalOpen, setLoginModalOpen] = useState(false);
    const [accessToken, setAccessToken] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState('');

    useEffect(() => {
            const accessToken = localStorage.getItem('accessToken');
            const isLoggedIn = localStorage.getItem('isLoggedIn');

            if(accessToken !== '' || accessToken !== null || accessToken !== undefined){
                setAccessToken(accessToken);
            }

            if(isLoggedIn !== '' || accessToken !== null || accessToken !== undefined || isLoggedIn !== false){
                setIsLoggedIn(isLoggedIn);
            }
      }, []);

    const handleLoginModalOpen = () => {
        setLoginModalOpen(true);
    };

    const handleLoginModalClose = () => {
        setLoginModalOpen(false);
    };

    return (
        <div className="header">
            <div className="logo-container">
                <img src={logo} alt="Logo" className="logo" />
                <h3>Doctor Finder</h3>
            </div>
            <div className="button-container">
                {isLoggedIn?
                <Button variant="contained" color="primary" onClick={handleLoginModalOpen}>{isLoggedIn}</Button>
                :
                <Button variant="contained" color="secondary">Logout</Button>
                }
            </div>
            <AuthenticationModal isOpen={isLoginModalOpen} handleClose={handleLoginModalClose} />
        </div>
    );
}

export default Header;
