import React, { useState, useEffect } from 'react';
import { Modal, Card, TextField, FormControl, Select, MenuItem, Button } from '@material-ui/core';

const BookAppointment = ({ open, handleClose, doctor_id, doctor_name }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [doctorId, setDoctorId] = useState(null);
  const [doctorName, setDoctorName] = useState(null);


  const getDocterIdAndName = (dr_id, dr_name) => {
    setDoctorId(dr_id);
    setDoctorName(dr_name);
  };
  useEffect(() => {
    console.log("Book appointment useEffect:")
    getDocterIdAndName(doctor_id, doctor_name);
    console.log("Chosen Doctor Name doctorName useEffect:", doctorId, doctorName);
  }, [open]); // Add doctor_name to the dependency array to watch for changes


  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeSlotChange = (event) => {
    setSelectedTimeSlot(event.target.value);
  };

  const handleConfirmAppointment = () => {
    // Your logic to check if the time slot is already booked or not available
    if (selectedTimeSlot === 'booked') {
      setErrorMessage('Either the slot is already booked or not available');
    } else {
      // Your logic to confirm the appointment
      handleClose();
    }
  };

  return (
    <Modal open={open} onClose={handleClose}>
      <Card style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', padding: '20px' }}>
        <h2>Book an Appointment</h2>
        <TextField label="Doctor's Name" value={doctorName} disabled fullWidth margin="normal" />
        <FormControl fullWidth margin="normal">
          <TextField
            label="Select Date"
            type="date"
            defaultValue={selectedDate}
            onChange={handleDateChange}
            InputLabelProps={{
              shrink: true,
            }}
          />
        </FormControl>
        <FormControl fullWidth margin="normal">
          <Select value={selectedTimeSlot} onChange={handleTimeSlotChange}>
            <MenuItem value="">Select Time Slot</MenuItem>
            {/* Your time slot options */}
            <MenuItem value="morning">Morning</MenuItem>
            <MenuItem value="afternoon">Afternoon</MenuItem>
            <MenuItem value="evening">Evening</MenuItem>
            {/* Add more time slot options as needed */}
          </Select>
          {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        </FormControl>
        <FormControl fullWidth margin="normal">
          <TextField label="Medical History" multiline rows={4} variant="outlined" />
        </FormControl>
        <FormControl fullWidth margin="normal">
          <TextField label="Symptoms" multiline rows={4} variant="outlined" />
        </FormControl>
        <Button variant="contained" color="primary" fullWidth onClick={handleConfirmAppointment}>
          Confirm Appointment
        </Button>
      </Card>
    </Modal>
  );
};

export default BookAppointment;
