// AuthenticationModal.js
import React, { useState } from 'react';
import { Modal, Tabs, Tab, Box } from '@material-ui/core';
import Login from '../../screens/login/Login';
import Register from '../../screens/register/Register';

const AuthenticationModal = ({ isOpen, handleClose }) => {
    const [activeTab, setActiveTab] = useState(0);

    const handleTabChange = (event, newValue) => {
        setActiveTab(newValue);
    };

    return (
        <Modal open={isOpen} onClose={handleClose}>
            <Box
                sx={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    bgcolor: 'background.paper',
                    boxShadow: 24,
                    p: 4,
                    width: 400,
                    maxWidth: '90%',
                    outline: 'none',
                }}
            >
                                <h2 style={{ color: 'white', backgroundColor:'red', marginBottom: '20px' }}>Authentication</h2>

                <Tabs value={activeTab} onChange={handleTabChange}>
                    <Tab label="Login" />
                    <Tab label="Register" />
                </Tabs>
                {activeTab === 0 && <Login />}
                {activeTab === 1 && <Register />}
            </Box>
        </Modal>
    );
}

export default AuthenticationModal;
