import React, { useEffect } from 'react';
import { Modal, Card, TextField, FormControl, Button } from '@material-ui/core';
import './RateAppointment.css';

const RateAppointment = ({ open, handleClose, doctorName, doctorId, appointmentId }) => {


    useEffect(() => {
      alert(doctorName)
      alert(doctorId)
      alert(appointmentId)
    }, []); // Add selectedDoctorName to the dependency array

    const handleLogin = async () => {
      // Reset errors
      setEmailError('');
      setPasswordError('');

      // Basic validation
      if (!email) {
          setEmailError('Please fill out this field');
          return;
      }
      if (!password) {
          setPasswordError('Please fill out this field');
          return;
      }

      try {
          // Construct basic authentication token
          const basicAuthToken = btoa(`${email}:${password}`);
          console.log(basicAuthToken);
          const response = await fetch('http://localhost:8080/auth/login', {
              method: 'POST',
              headers: {
                  'Authorization': `Basic ${basicAuthToken}`,
                  'Content-Type': 'application/json',
              }
          });

          // Retrieve the access token from the response
          const data = await response.json();
          if (!response.ok) {
              setPasswordError(data.message);
              return;
          }
          const accessToken = data.accessToken.trim();
          const userId = data.id;
          const userName = btoa(`${data.firstName} ${data.lastName}`); 
          const userEmailId = data.emailAddress;
          console.log(data);
          // Store the access token in localStorage or sessionStorage
          localStorage.setItem('accessToken', accessToken);
          localStorage.setItem('isLoggedIn', true);
          localStorage.setItem('userId', userId);
          localStorage.setItem('userName', userName);
          localStorage.setItem('userEmailId', userEmailId);

          // Navigate to the home page
          // code to be implemented

      } catch (error) {
          console.error('Login failed:', error);
          setPasswordError('Failed to Login in');
      }
  };

  return (
    <Modal open={open} onClose={handleClose}>
      <Card style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', padding: '20px' }}>
        <h2>Rate an Appointment</h2>
        <TextField label="Comments" value={doctorName} disabled fullWidth margin="normal" />
        <FormControl fullWidth margin="normal">
        <TextField label="Rating" variant="outlined" />
        </FormControl>
        <Button variant="contained" color="primary" fullWidth onClick={handleRateAppointment}>
          Rate Appointment
        </Button>
      </Card>
    </Modal>
  );
};

export default RateAppointment;
