// Register.js
import React, { useState, useRef, useEffect } from 'react';
import { FormControl, InputLabel, Input, Button, Paper } from '@material-ui/core';
import './Register.css'

const Register = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [contactNumber, setContactNumber] = useState('');
    const [firstNameError, setFirstNameError] = useState('');
    const [lastNameError, setLastNameError] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [contactNumberError, setContactNumberError] = useState('');
    const [registrationMessage, setRegistrationMessage] = useState('');

    const firstNameInputRef = useRef(null);
    const lastNameInputRef = useRef(null);
    const emailInputRef = useRef(null);
    const passwordInputRef = useRef(null);
    const contactNumberInputRef = useRef(null);

    useEffect(() => {
        if (firstNameError) {
            firstNameInputRef.current.focus();
        }
        if (firstNameError) {
            lastNameInputRef.current.focus();
        }
        if (firstNameError) {
            emailInputRef.current.focus();
        }
        if (firstNameError) {
            passwordInputRef.current.focus();
        }
        if (firstNameError) {
            contactNumberInputRef.current.focus();
        }
    }, [firstNameError]);


    const handleRegister = async () => {
        // Reset errors and messages
        setFirstNameError('');
        setLastNameError('');
        setEmailError('');
        setPasswordError('');
        setContactNumberError('');
        setRegistrationMessage('');

        // Basic validation
        if (!firstName || !lastName || !email || !password || !contactNumber) {
            setRegistrationMessage('Please fill out all the fields.');
            if (!firstName) {
                setFirstNameError('First name is required.');
            }
            return;
        }

        if (!isValidContactNumber(contactNumber)) {
            setContactNumberError('Enter a valid mobile number.');
            return;
        }

        try {
            const signInData = {
                firstName: firstName,
                lastName: lastName,
                mobile: contactNumber,
                password: password,
                emailId: email
            };

            const response = await fetch('http://localhost:8080/users/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(signInData),
            });

            if (!response.ok) {
                throw new Error('Registration failed. Please try again.');
            }

            // Registration successful
            setRegistrationMessage('User registered successfully');
            // Clear input fields after successful registration
            setFirstName('');
            setLastName('');
            setEmail('');
            setPassword('');
            setContactNumber('');
        } catch (error) {
            console.error('Registration failed:', error.message);
            setRegistrationMessage(error.message || 'Registration failed. Please try again.');
        }
    };



    const isValidContactNumber = (contactNumber) => {
        // Regular expression for basic mobile number validation
        // You can enhance it as per your requirements
        const contactNumberRegex = /^\d{10}$/;
        return contactNumberRegex.test(contactNumber);
    };

    return (
        <div className="register-form">
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="firstName">First Name *</InputLabel>
                <Input
                    id="firstName"
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    inputRef={firstNameInputRef}
                />
                {firstNameError && 
                    <Paper className="error-popup" elevation={3} style={{ backgroundColor: 'black', color: 'white' }}>
                        <span>{firstNameError}</span>
                    </Paper>
                }
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="lastName">Last Name *</InputLabel>
                <Input
                    id="lastName"
                    type="text"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    // error={!!lastNameError}
                    inputRef={lastName}
                />
                {lastNameError && 
                    <Paper className="error-popup" elevation={3} style={{ backgroundColor: 'black', color: 'white' }}>
                        <span>{lastNameError}</span>
                    </Paper>
                }
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="email">Email *</InputLabel>
                <Input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    // error={!!emailError}
                />
                {/* <span className="error">{emailError}</span> */}
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="password">Password *</InputLabel>
                <Input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    // error={!!passwordError}
                />
                {/* <span className="error">{passwordError}</span> */}
            </FormControl>
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="contactNumber">Mobile No. *</InputLabel>
                <Input
                    id="contactNumber"
                    type="text"
                    value={contactNumber}
                    onChange={(e) => setContactNumber(e.target.value)}
                    // error={!!contactNumberError}
                />
                {/* <span className="error">{contactNumberError}</span> */}
            </FormControl>
            <Button variant="contained" className="registerButton" style={{ margin: 'auto', display: 'block', marginTop:'25px' }} color="primary" onClick={handleRegister}>
                REGISTER
            </Button>
            {/* <span className="success">{registrationMessage}</span> */}
        </div>
    );
};

export default Register;
