// Register.js
import React, { useState } from 'react';
import { FormControl, InputLabel, Input, Button } from '@material-ui/core';

const Register = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [contactNumber, setContactNumber] = useState('');
    const [firstNameError, setFirstNameError] = useState('');
    const [lastNameError, setLastNameError] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [contactNumberError, setContactNumberError] = useState('');
    const [registrationMessage, setRegistrationMessage] = useState('');

    const handleRegister = () => {
        // Reset errors and messages
        setFirstNameError('');
        setLastNameError('');
        setEmailError('');
        setPasswordError('');
        setContactNumberError('');
        setRegistrationMessage('');

        // Basic validation
        if (!firstName) {
            setFirstNameError('Please fill out this field');
        }
        if (!lastName) {
            setLastNameError('Please fill out this field');
        }
        if (!email) {
            setEmailError('Please fill out this field');
        }
        if (!password) {
            setPasswordError('Please fill out this field');
        }
        if (!contactNumber) {
            setContactNumberError('Please fill out this field');
        } else if (!isValidContactNumber(contactNumber)) {
            setContactNumberError('Enter valid mobile number');
        }

        // Call your backend API endpoint '/users/register' here
        // If registration successful, you can handle it accordingly
        // For example, display a success message
        setRegistrationMessage('Registration Successful');
    };

    const isValidContactNumber = (contactNumber) => {
        // Regular expression for basic mobile number validation
        // You can enhance it as per your requirements
        const contactNumberRegex = /^\d{10}$/;
        return contactNumberRegex.test(contactNumber);
    };

    return (
        <div className="register-form">
            <FormControl fullWidth margin="normal">
                <InputLabel htmlFor="firstName">First Name *</InputLabel>
                <Input
                    id="firstName"
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    error={!!firstNameError}
                />
                <span className="error">{firstNameError}</span>
            </FormControl>
            {/* Other form fields */}
            <Button variant="contained" color="primary" onClick={handleRegister} style={{ margin: 'auto', display: 'block' }}>
                REGISTER
            </Button>
            {registrationMessage && <p>{registrationMessage}</p>}
        </div>
    );
};

export default Register;
