import React, { useState } from 'react';
import { Modal, FormControl, InputLabel, Input, Button } from '@material-ui/core';
import './Login.css';

const Login = ({ isOpen, handleClose }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');

    const handleLogin = () => {
        // Reset errors
        setEmailError('');
        setPasswordError('');

        // Basic validation
        if (!email) {
            setEmailError('Please fill out this field');
        }
        if (!password) {
            setPasswordError('Please fill out this field');
        }

        // Call your backend API endpoint '/auth/login' here
        // If login successful, you can handle it accordingly
    };

    const isValidEmail = (email) => {
        // Regular expression for basic email validation
        // You can enhance it as per your requirements
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    return (
        <Modal open={isOpen} onClose={handleClose}>
            <div className="login-modal">
                <h2>Login</h2>
                <FormControl fullWidth margin="normal">
                    <InputLabel htmlFor="email">Email</InputLabel>
                    <Input
                        id="email"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        error={!!emailError}
                    />
                    <span className="error">{emailError}</span>
                </FormControl>
                <FormControl fullWidth margin="normal">
                    <InputLabel htmlFor="password">Password</InputLabel>
                    <Input
                        id="password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        error={!!passwordError}
                    />
                    <span className="error">{passwordError}</span>
                </FormControl>
                <Button variant="contained" color="primary" onClick={handleLogin}>
                    LOGIN
                </Button>
            </div>
        </Modal>
    );
}

export default Login;
