import React, { useState, useEffect } from 'react';
import { Modal, Card, TextField, FormControl, Select, MenuItem, Button } from '@material-ui/core';

const BookAppointment = ({ open, handleClose, doctor_id, doctor_name }) => {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]); // Today's date
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [doctorId, setDoctorId] = useState(null);
  const [doctorName, setDoctorName] = useState(null);
  const [timeSlots, setTimeSlots] = useState([]);
  const [symptoms, setSymptoms] = useState('');
  const [medicalHistory, setMedicalHistory] = useState('');
  const [userDetails, setUserDetails] = useState('');

  const getDocterName = (doctor_name) => {
    if(doctor_name === null || doctor_name === undefined){
      const choosenDoctorId = localStorage.getItem('choosenDoctorId');
      const choosenDoctorName = localStorage.getItem('choosenDoctorName');
      setDoctorId(choosenDoctorId);
      setDoctorName(choosenDoctorName);
    } else {
      setDoctorId(doctor_id);
      setDoctorName(doctor_name);
    }
  };

  useEffect(() => {
    getDocterName(doctor_id, doctor_name);
  }, [open]);

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeSlotChange = (event) => {
    setSelectedTimeSlot(event.target.value);
  };

  const fetchTimeSlots = async () => {
    try {
      const response = await fetch(`http://localhost:8080/doctors/${doctorId}/timeSlots?date=${selectedDate}`);
      const data = await response.json();
      setTimeSlots(data.timeSlot);
    } catch (error) {
      console.error('Error fetching time slots:', error);
    }
  };

  useEffect(() => {
    fetchTimeSlots();
  }, [selectedDate]);

  const getUserDetails = async () => {
    try {
      const userEmail = localStorage.getItem('userEmailId');
      // const token = localStorage.getItem('accessToken'); // Assuming the token is stored as 'accessToken' in localStorage
      // console.log("token")
      // console.log(token)

      const token="eyJraWQiOiI5NTM2YWZhOC0wZjYxLTQ5YjctOWI5NC00OWQyNzY5OTMwY2QiLCJ0eXAiOiJKV1QiLCJhbGciOiJIUzUxMiJ9.eyJhdWQiOiJzb251QGdtYWlsLmNvbSIsImlzcyI6Imh0dHBzOi8vYm9va215Y29uc3VsdGF0aW9uLmNvbSIsImV4cCI6MTcwNzI1MSwiaWF0IjoxNzA3MjIzfQ.6aSLq6mlFYPqiDDKx4-yqsWIKnFqNUn-Hv5ol53LA2RL6oNX9oVnCRJ_mVG_qFGa8SmCiRS3GSkCYTby7WcE-g"

      const response = await fetch(`http://localhost:8080/users/${userEmail}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json' // Adjust the content type as per your API requirements
        }
      });
      const data = await response.json();
      console.log("insdie");
      console.log(data)
      setUserDetails(data);
    } catch (error) {
      console.log(error);
    }
  };


  const handleConfirmAppointment = async () => {
    // Check if a time slot is selected
    if (selectedTimeSlot === '') {
      setErrorMessage('Please select a time slot');
      return;
    }
    getUserDetails();
    console.log("userData");
    console.log(userDetails);
    // const user_id = localStorage.getItem('userId');
    // const user_name = localStorage.getItem('userName');
    // const user_emailId = localStorage.getItem('userEmailId');
    // // Prepare the appointment data
    // const appointmentData = {
    //   doctorId: doctorId,
    //   doctorName: doctorName,
    //   userId: user_id,
    //   userName: user_name,
    //   userEmailId: user_emailId,
    //   timeSlot: selectedTimeSlot,
    //   appointmentDate: selectedDate,
    //   createdDate: new Date().toISOString().split('T')[0],
    //   symptoms: symptoms,
    //   priorMedicalHistory: medicalHistory
    // };

    // try {
    //   const basicAuthToken = localStorage.getItem('accessToken');
    //   console.log(basicAuthToken);
      // Send a POST request to the API endpoint
      // const response = await fetch('http://localhost:8080/appointments', {
      //   method: 'POST',
      //   headers: {
      //     'Authorization': `Basic ${basicAuthToken}`,
      //     'Content-Type': 'application/json'
      //   },
      //   body: JSON.stringify(appointmentData)
      // });

      // // Check if the request was successful
      // if (response.ok) {
      //   // Appointment confirmed, close the modal
      //   handleClose();
      // } else {
      //   // Handle error response
      //   const errorData = await response.json();
      //   console.error('Error confirming appointment:', errorData);
      //   setErrorMessage('Error confirming appointment');
      // }
    // } catch (error) {
    //   console.error('Error confirming appointment:', error);
    //   setErrorMessage('Error confirming appointment');
    // }
  };  

  return (
    <Modal open={open} onClose={handleClose}>
      <Card style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', padding: '20px' }}>
        <h2>Book an Appointment</h2>
        <TextField
          label="Doctor's Name"
          value={doctorName}
          disabled
          fullWidth
          margin="normal"
        />
        <FormControl fullWidth margin="normal">
          <TextField
            label="Select Date"
            type="date"
            value={selectedDate}
            onChange={handleDateChange}
            InputLabelProps={{
              shrink: true,
            }}
            inputProps={{
              min: new Date().toISOString().split('T')[0], // Set min to today's date
            }}
          />
        </FormControl>
        <FormControl fullWidth margin="normal">
          <Select value={selectedTimeSlot} onChange={handleTimeSlotChange}>
            <MenuItem value="">Select Time Slot</MenuItem>
            {timeSlots ? (
              timeSlots.map((slot, index) => (
                <MenuItem key={index} value={slot}>{slot}</MenuItem>
              ))
            ) : (
              <MenuItem value="" disabled>No available time slots</MenuItem>
            )}
          </Select>
          {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        </FormControl>
        <FormControl fullWidth margin="normal">
          <TextField label="Medical History" value={medicalHistory} multiline rows={4} variant="outlined" />
        </FormControl>
        <FormControl fullWidth margin="normal">
          <TextField label="Symptoms" value={symptoms} multiline rows={4} variant="outlined" />
        </FormControl>
        <Button variant="contained" color="primary" fullWidth onClick={handleConfirmAppointment}>
          Confirm Appointment
        </Button>
      </Card>
    </Modal>
  );
};

export default BookAppointment;
