import React, { useState } from 'react';
import { Paper, Typography, Button } from '@material-ui/core';
import BookAppointment from './BookAppointment'; // Import BookAppointment component
import DoctorDetails from './DoctorDetails'; // Import DoctorDetails component
import './DoctorList.css'; // Import your DoctorList CSS file

const Appointment = ({ doctor }) => {

  //using doctor uuid fetch all pointmetns

  const doctorsAppointment = [
    { name: 'Devraj', speciality: 'Cardiologist', rating: 4.5 },
    { name: 'Raman', speciality: 'Dermatologist', rating: 4.8 },
    { name: 'Surya', speciality: 'Pediatrician', rating: 4.3 },
    { name: 'Gowri', speciality: 'Pediatrician', rating: 4.3 },
  ];

  return (
    <div className="DoctorsTab">
      <label>Select Speciality: </label>
      <select value={selectedSpeciality} onChange={handleSpecialityChange} className="speciality-dropdown">
        <option value="">All Specialities</option>
        {Array.from(new Set(doctors.map((doctor) => doctor.speciality))).map((speciality, index) => (
          <option key={index} value={speciality}>{speciality}</option>
        ))}
      </select>
      {doctorsAppointments.map((doctorAppo, index) => (
        <Paper key={index} elevation={3} className="doctor-paper">
          <Typography variant="h6">Doctor Name: {doctor.name}</Typography><br/>
          <Typography variant="subtitle1">Speciality: {doctor.speciality}</Typography>
          <Typography variant="subtitle1">Average Rating: {doctor.rating}</Typography>
          <div className="button-container">
            <Button 
              variant="contained" 
              color="primary" 
              className="book-appointment-button" 
              onClick={() => handleBookAppointment(doctor)} // Handle booking appointment
            >
              Book Appointment
            </Button>
            <Button 
              variant="contained" 
              style={{ backgroundColor: 'green', color: 'white' }} 
              className="view-details-button"
              onClick={() => handleViewDetails(doctor)} // Handle viewing details
            >
              View Details
            </Button>
          </div>
        </Paper>
      ))}
      </div>
  );
};

export default Appointment;
