import React, { useState } from 'react';
import { Paper, Typography, Button } from '@material-ui/core';
import RateAppointment from './RateAppointment'; // Import the RateAppointment component

const Appointment = ({ doctor }) => {
  const [isRateAppointmentOpen, setIsRateAppointmentOpen] = useState(false);

  const handleRateAppointmentClick = () => {
    setIsRateAppointmentOpen(true);
  };

  const handleRateAppointmentClose = () => {
    setIsRateAppointmentOpen(false);
  };

  //using doctor uuid fetch all pointmetns

  // current time > data then only show appointments

  const doctorsAppointments = [
    { name: 'Devraj', date: '21-02-2024', symptoms: "Fever", priorMedicalHistory: "abc" },
    { name: 'Raman', date: '21-02-2024', symptoms: "Cough", priorMedicalHistory: "abc" },
    { name: 'Surya', date: '2-02-2024', symptoms: "Headache, fever", priorMedicalHistory: "abc" },
    { name: 'Gowri', date: '12-09-2024', symptoms: "Eye pain, neck pain", priorMedicalHistory: "abc" },
  ];

  const handleRateAppointment = (doctor, appointmentID)  => {
    //
  };

  return (
    <div className="DoctorsAppointmentTab">
      {doctorsAppointments.map((doctorAppointment, index) => (
        <Paper key={index} elevation={3} className="doctor-appointment-paper">
          <Typography variant="h6">Dr. {doctorAppointment.name}</Typography>
          <Typography variant="subtitle1">Date: {doctorAppointment.date}</Typography>
          <Typography variant="subtitle1"> Symptoms: {doctorAppointment.symptoms}</Typography>
          <Typography variant="subtitle1">priorMedicalHistory: {doctorAppointment.priorMedicalHistory}</Typography><br/>
          <div className="rate-appointment">
            <Button 
              variant="contained" 
              color="primary" 
              className="rate-appointment-button" 
              onClick={handleRateAppointmentClick} // Open RateAppointment modal
            >
              Rate Appointment
            </Button>
          </div>
        </Paper>
      ))}
      <RateAppointment open={isRateAppointmentOpen} handleClose={handleRateAppointmentClose} doctorName={doctor.name} />
    </div>
  );
};

export default Appointment;
