import React, { useState } from 'react';
import { Modal, Card, TextField, FormControl, Button } from '@material-ui/core';

const RateAppointment = ({ open, handleClose, doctorName }) => {
  const handleRateAppointment = () => {
    // Implement the logic to rate the appointment
    handleClose(); // Close the modal after rating the appointment
  };


  return (
    <Modal open={open} onClose={handleClose}>
      <Card style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', padding: '20px' }}>
        <h2>Rate an Appointment</h2>
        <TextField label="Comments" value={doctorName} disabled fullWidth margin="normal" />
        <FormControl fullWidth margin="normal">
          <TextField
            label="Select Date"
            type="date"
            defaultValue={selectedDate}
            onChange={handleDateChange}
            InputLabelProps={{
              shrink: true,
            }}
          />
        </FormControl>
        <FormControl fullWidth margin="normal">
          <TextField label="Rating" multiline rows={4} variant="outlined" />
        </FormControl>
        <Button variant="contained" color="primary" fullWidth onClick={handleRateAppointment}>
          Rate Appointment
        </Button>
      </Card>
    </Modal>
  );
};

export default RateAppointment;
